extension.install:
// Setup main table
if (!$forum_db->table_exists('hcs_redirect_links'))
{
	$schema = array(
		'FIELDS'			=> array(
			'id'				=> array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'counter'			=> array(
				'datatype'		=> 'INT(10) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			),
			'link'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'post_id'			=> array(
				'datatype'		=> 'INT(10) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			),
			'user_id'			=> array(
				'datatype'		=> 'INT(10) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			),
		),
		'PRIMARY KEY'	=> array('id'),
		'INDEXES'		=> array(
			'link_idx'		=> array('link', 'post_id', 'user_id' )
		)
	);
	$forum_db->create_table('hcs_redirect_links', $schema);
}

$query = array(
	'SELECT'	=> 'conf_name',
	'FROM'		=> 'config',
	'WHERE'		=> 'conf_name = \'o_hcs_redirect_links\'',
);

$result = $forum_db->query_build($query) or error(__FILE__, __LINE__);

if (!$forum_db->num_rows($result)) {
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_hcs_redirect_links\', \'\''
	);

	$forum_db->query_build($query) or error(__FILE__, __LINE__);

}

hooks.hook:
if (isset($forum_page['url']))
{

	if (!isset($lang_redirect_links))
	{
		if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'.php'))
			require_once $ext_info['path'].'/lang/'.$forum_user['language'].'.php';
		else
			require_once $ext_info['path'].'/lang/English.php';
	}

	$hash = forum_link($forum_url['hcs_redirect_uid'], array(forum_hash($user_url, ''),$user['id']));
	$forum_page['url'] = '<a href="'.$hash.'" class="external url" rel="me">'.$lang_redirect_links['External link'].'</a>';
	$forum_page['user_contact']['website'] = '<li><span>'.$lang_profile['Website'].': '.$forum_page['url'].'</span></li>';
}
